import React, { useState, useContext } from "react";
import { AppContext } from './AppContext';
import './style.css';
class Node {
  constructor(id,duration,ot,mt,pt) {
    this.id = id;
    this.duration =duration;
    this.next = [];
    this.prev = [];
    this.es = 0;
    this.ef = 0;
    this.ls = 0;
    this.lf = 0;
    this.et = (ot + (4 * mt) + pt)/6
  }
}

class NodeTree {
  constructor() {
    this.nodes = {};
  }
  
  append(id, prevIds,duration,ot,mt,pt) {
    //este codigo agrega un nodo, solo requiere de los nodos predecesores y de esa forma apunta hacia los nodos hacia adelante y hacia atras
    const newNode = new Node(id,duration,ot,mt,pt);
    this.nodes[id] = newNode;
    prevIds.forEach(prevId=> {
      if(this.nodes[prevId]){
        newNode.prev.push(this.nodes[prevId])
      }
    })
    this.pile = [newNode]
    link();
    if(!this.first){
      this.first = newNode; //primer nodo del arbol
    }
    //look for first node
    this.pile = [] 
    this.pile.push(this.first);
    this.forward();
    this.pile = [] 
    this.pile.push(this.last);
    this.backward();
  }
  link(){
    if(this.pile.length===0){

    } else {
      let aux = this.pile[0];
      aux.prev.forEach(node =>{
        this.pile.push(node);
      }
      )
      if(aux.prev){
        aux.prev.next = aux;
      }
      this.pile.shift();
      this.backward();
    }

  }
  forward(){
    
    if(this.pile.length===0){
      
    } else {
      let aux = this.pile[0];
      aux.next.forEach(node =>{
        this.pile.push(node);
      }
      )
      if(aux.prev.length === 0){
        aux.es = 0;
        aux.ef = aux.duration;
      }else{
        let maxes = 0;
        aux.prev.forEach(p => {
          if(Number(p.ef) > Number(maxes)){
            maxes = p.ef;
          }
        })
        aux.es = maxes;
        aux.ef = Number(maxes) + Number(aux.duration);
      } 
      this.last = this.pile[0];
      this.prduration = this.last.ef;
      this.pile.shift();
      this.forward();
    }
  }
  backward(){
    if(this.pile.length===0){

    } else {
      let aux = this.pile[0];
      aux.prev.forEach(node =>{
        this.pile.push(node);
      }
      )
      if(aux.next.length === 0){
        aux.lf = this.prduration;
        aux.ls = aux.lf - aux.duration;
      }else{
        let mines = aux.next[0].ls;
        aux.next.forEach(p => {
          if(Number(p.ls) < Number(mines)){
            mines = p.ls;
          }
        })
        aux.lf = mines;
        aux.ls = Number(aux.lf) - Number(aux.duration);
      } 
      this.pile.shift();
      this.backward();
    }
  }
  
  display() {
    Object.values(this.nodes).forEach(node => {
      console.log(`Node ${node.id} has next nodes with ids:`, node.next.map(nextNode => nextNode.id));
      console.log(`Node ${node.id} has ef of ${node.ef}`);
      console.log(`Node ${node.id} has lf of ${node.lf}`);
    });
  }
}
function Pert(){
const tree = new NodeTree();
const { tasklist, dispatch, loading } = useContext(AppContext);
function generatePert(){
  tasklist.forEach((task)=>{
    tree.append(task.id,task.preid,task.time,task.ot,task.mt,task.pt);
  })
  let aux = tree.nodes[1];
  const k = 100;
  const divs = [];
  divs.push(<div class="node" style={{left:`${100}px`,top:`${0}%`}}>{aux.id}</div>);
  let x= 2*k;
  while(aux){
    let y =0;
    console.log(aux);
    if(aux.next){
      
      aux.next.forEach((link)=>{
        if(y==0){
        divs.push(<div class="line" style={{left:`${x-70}px`,top:`${y+25}px`,width:`${k}px`}} ></div>);
        } else {
          let x1=x,x2=x-100,y1=y,y2=0;
          divs.push(<div class="line" style={{
            position: 'absolute',
            left: `${x1+30}px`,
            top: `${y1+40}px`,
            width: `${Math.sqrt(Math.pow(x2 - x1, 2) + Math.pow(y2 - y1, 2))}px`, // Calculate the distance between points
            height: '0px', // Adjust line thickness as needed
            backgroundColor: 'black',
            transformOrigin: 'top left',
            transform: `rotate(${Math.atan2(y2 - y1, x2 - x1)}rad)`, // Calculate the angle of rotation
          }} ></div>)
        }
        divs.push(<div class="node" style={{left:`${x}px`,top:`${y}px`}}>{link.id}</div>);
        
        y=y+50;
      })
    
  }
  aux = aux.next[0];
  x=x+k;
  //console.log(aux);  
  }
  tree.display();
  return divs;
}
return (

    <div class="container" >{loading ? (
      <div>Loading...</div>
    ) : (generatePert())}</div>
);
}

export default Pert;
