import React, { createContext, useReducer } from 'react';
// 5. The reducer - this is used to update the state, based on the action
export const AppReducer = (state, action) => {
    switch (action.type) {
        case 'ADD_TASK':
            state.tasklist = state.tasklist.push(action.payload)
            action.type = "DONE";
            return {
                ...state,
            };
        case 'DELETE_TASK':
            state.tasklist.map((task)=>{
                if(task.text === action.payload.text) {
                } else {
                state.tasklist.push(task);
                }
                return true;
            })
            action.type = "DONE";
            return {
                ...state,
            };
    
        default:
            return state;
    }
};

// 1. Sets the initial state when the app loads

const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:5000'); // Replace with your API endpoint
      if (!response.ok) {
        throw new Error('Failed to fetch data');
      }
      return await response.json();
    } catch (error) {
      console.error('Error fetching data:', error);
      return {}; // Return empty object as default data if fetch fails
    }
  };
const initialState = fetchData();
// 2. Creates the context this is the thing our components import and use to get the state
export const AppContext = createContext();
// 3. Provider component - wraps the components we want to give access to the state
// Accepts the children, which are the nested(wrapped) components
export const AppProvider = (props) => {
    // 4. Sets up the app state. takes a reducer, and an initial state
    const [state, dispatch] = useReducer(AppReducer, initialState);
    const { tasklist } = state;
    return (
        <AppContext.Provider
            value={{
                tasklist,
                dispatch
            }
            }
        >
            {props.children}
        </AppContext.Provider>
    );
};
