import React, { createContext, useState, useEffect, useReducer } from 'react';

// Define your context
const AppContext = createContext();
export default AppContext;

// Define your reducer function
export const AppReducer = (state, action) => {
    switch (action.type) {
        case 'ADD_TASK':
            return {
                ...state,
                tasklist: [...state.tasklist, action.payload]
            };
        case 'DELETE_TASK':
            return {
                ...state,
                tasklist: state.tasklist.filter(task => task.text !== action.payload.text)
            };
        default:
            return state;
    }
};

// Create your provider component
export const AppProvider = (props) => {
    const [state, dispatch] = useReducer(AppReducer, { tasklist: [{"id":1714973839897,"text":"hey","time":"30","stdate":"1","completed":false}] });
    const [loading, setLoading] = useState(true);

   
    return (
        <AppContext.Provider value={{tasklist:state.tasklist,state, dispatch, loading }}>
            {props.children}
        </AppContext.Provider>
    );
};
