import React, { useState, useContext } from "react";
import { AppContext } from './AppContext';
import './style.css';
class Node {
  constructor(id) {
    this.id = id;
    this.next = [];
  }
}

class NodeTree {
  constructor() {
    this.nodes = {};
  }

  append(id, nextIds) {
    const newNode = new Node(id);
    nextIds.forEach(nextId => {
      if (this.nodes[nextId]) {
        this.nodes[nextId].next.push(newNode);
      }
    });
    this.nodes[id] = newNode;
  }

  display() {
    Object.values(this.nodes).forEach(node => {
      console.log(`Node ${node.id} has next nodes with ids:`, node.next.map(nextNode => nextNode.id));
    });
  }
}
function Pert(){
const tree = new NodeTree();
const { tasklist, dispatch, loading } = useContext(AppContext);
function generatePert(){
  tasklist.forEach((task)=>{
    tree.append(task.id,[task.preid]);
  })
  let aux = tree.nodes[1];
  divs.push(<div class="circulo">{aux.id}</div>);
  console.log(aux);
  const divs = [];
  while(aux!=null){
    if(aux.next!=null){
    aux.next.forEach((link)=>{
      divs.push(<div class="circulo">{link.id}</div>);
    })
    aux = aux.next;
  }
    
  }
  
  return divs;
}
return (

    <div class="contenedor" >{loading ? (
      <div>Loading...</div>
    ) : (generatePert())}</div>
);
}

export default Pert;
