import React, { useState, useContext } from "react";
import { AppContext } from './AppContext';
import './style.css';
class Node {
  constructor(id) {
    this.id = id;
    this.next = [];
  }
}

class NodeTree {
  constructor() {
    this.nodes = {};
  }

  append(id, nextIds) {
    const newNode = new Node(id);
    nextIds.forEach(nextId => {
      if (this.nodes[nextId]) {
        newNode.next.push(this.nodes[nextId]);
      }
    });
    this.nodes[id] = newNode;
  }

  display() {
    Object.values(this.nodes).forEach(node => {
      console.log(`Node ${node.id} has next nodes with ids:`, node.next.map(nextNode => nextNode.id));
    });
  }
}
function Pert(){
const tree = new NodeTree();
const { tasklist, dispatch, loading } = useContext(AppContext);
function generatePert(){
  let linkedtasks;
  for(let task in tasklist){
    tree.append(task.id,[task.preid]);
  }
  let aux = tree.nodes[0];
  const divs = [];
  while(aux!=null){
    for(let link in aux.next){
      divs.push(<div class="circulo">{link.id}</div>);
    }
  }
  
  return divs;
}
return (

    <div class="contenedor" >{loading ? (
      <div>Loading...</div>
    ) : (generatePert())}</div>
);
}

export default Pert;
