import React, { useState, useContext } from "react";
import { AppContext } from './AppContext';

function Taskgen() {
  const { tasklist, dispatch, loading } = useContext(AppContext);
  const [tasks, setTasks] = useState([]);
function generateDivs(startDate, endDate){
  const divs = [];
  for (let i = 0; i < 7; i++) {
    if(i >= startDate && i<=endDate){
      divs.push(<td bgcolor="green" key={i}>Div {i + 1}</td>);
    } else {
      divs.push(<td key={i}>Div {i + 1}</td>);
    }
  }
  return divs;
  }

  function handleSubmit(e) {
    e.preventDefault();

    let task = document.getElementById('taskAdd').value;
    let time = document.getElementById('time').value;
    let stdate = document.getElementById('stdate').value;
    let enddate  = document.getElementById('enddate').value;
    let preid = document.getElementById('preid').value;
    let id = 0;
    try{
      id = 1+tasklist[tasklist.length-1].id;
    } catch(error) {
      id =0;
    }
    const newTask = {
      id: id,
      text: task.trim(),
      time: time,
      stdate: stdate,
      enddate : enddate,
      preid : preid,
      completed: false,
    };
    if (newTask.text.length > 0) {
      const postData = async () => {
        try {
          const response = await fetch('http://localhost:5000/task', {
            method: 'POST',
            mode: 'cors',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify(newTask)
          });
          const data = await response.json();
          console.log('Response:', data);
        } catch (error) {
          console.error('Error:', error);
          console.log('Response:', error.response);
          alert(error);
        }
      };
      postData();
      dispatch({
        type: 'ADD_TASK',
        payload: newTask,
      });
      setTasks([...tasks].concat(newTask));
    } else {
      alert("Enter Valid Task");
    }
    document.getElementById('taskAdd').value = "";
    document.getElementById('time').value = "";
    document.getElementById('stdate').value = "";
    document.getElementById('enddate').value = "";
    document.getElementById('preid').value = "";
  }

  return (
    <div>
      <div id="todo-list">
        <h1>Todo List</h1>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            id='taskAdd'
            placeholder="Enter your task here"
          />
          <input
            type="number"
            id="time"
            placeholder="Enter the time length"
          />
          <input
            type="number"
            id="stdate"
            placeholder="Enter the start date"
          />
          <input
            type="number"
            id="enddate"
            placeholder="Enter the end date"
          />
          <input
            type="number"
            id="preid"
            placeholder="Enter the id of the preceeding task"
          />
          <button type="submit">Add Todo</button>
        </form>
        <table>
        <tr>
          <th>Task</th>
          <th>Length</th>
          <th>St.Date</th>
          <th>EndDate</th>
          <th>Preceeding task</th>
        </tr>
        {loading ? (
          <div>Loading...</div>
        ) : (

          tasklist.map((task) => (
            <tr key={task.id}>
              <td className="todo-id">{task.id}</td>
              <td className="todo-text">{task.text}</td>
              <td className="time-num">{task.time}</td>
              <td className="stdate">{task.stdate}</td>
              <td className="preid">{task.preid}</td>
              {generateDivs(task.stdate,task.enddate)}
              {/* insert delete button below this line */}
            </tr>
          ))

        )}
        </table>
      </div>
    </div>
     );
}

export default Taskgen;
