import './App.css';
import React, {useState} from "react";

function App() {
  const [tasks, setTasks] = useState([]);
  function handleSubmit(e) {
    e.preventDefault();
    let task = document.getElementById('taskAdd').value
    let time = document.getElementById('time').value
    const newTask = {
      id: new Date().getTime(),
      text: task.trim(),
      time: time,
      completed: false,
    };
    if (newTask.text.length > 0 ) {
      const postData = async () => {
        try {
          const response = await fetch('http://localhost:5000/task', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify(newTask) // Replace with your data
          });
          const data = await response.json();
          setMessage(data.message);
        } catch (error) {
          console.error('Error:', error);
        }
      };
        setTasks([...tasks].concat(newTask));
    } else {
        alert("Enter Valid Task");
    }
    document.getElementById('taskAdd').value = ""
    document.getElementById('time').value = 0
  }
  return (
    <div >
          Welcome to whatever this is.
      <div id="todo-list">
        <h1>Todo List</h1>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              id = 'taskAdd'
            />
            <input
              type="number"
              id = "time"
            />
            <button type="submit">Add Todo</button>
          </form>
          {tasks.map((task) =>
            <div key={task.id}>
                <div className="todo-text">{task.text}</div>
                <div className="time-num">{task.time}</div>
            {/* insert delete button below this line */}
            </div>)}
      </div>
    </div>

  );
}

export default App;
