import React, { useState, useContext } from "react";
import { AppContext } from './AppContext';

function Taskgen() {
  const { tasklist, dispatch, loading } = useContext(AppContext);
  const [tasks, setTasks] = useState([]);
  function DivGenerator({ n }) {
    // Create an array of length n
    const divs = Array.from({ length: n }, (_, index) => (
      <div key={index} className="generated-div">
        Div {index + 1}
      </div>
    ));

    return <>{divs}</>;
  }

  function handleSubmit(e) {
    e.preventDefault();

    let task = document.getElementById('taskAdd').value;
    let time = document.getElementById('time').value;
    let stdate = document.getElementById('stdate').value;
    const newTask = {
      id: new Date().getTime(),
      text: task.trim(),
      time: time,
      stdate: stdate,
      completed: false,
    };
    if (newTask.text.length > 0) {
      const postData = async () => {
        try {
          const response = await fetch('http://localhost:5000/task', {
            method: 'POST',
            mode: 'cors',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify(newTask)
          });
          const data = await response.json();
          console.log('Response:', data);
        } catch (error) {
          console.error('Error:', error);
          console.log('Response:', error.response);
          alert(error);
        }
      };
      postData();
      dispatch({
        type: 'ADD_TASK',
        payload: newTask,
      });
      setTasks([...tasks].concat(newTask));
    } else {
      alert("Enter Valid Task");
    }
    document.getElementById('taskAdd').value = "";
    document.getElementById('time').value = "";
    document.getElementById('stdate').value = "";
  }

  return (
    <div>
      <div id="todo-list">
        <h1>Todo List</h1>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            id='taskAdd'
            placeholder="Enter your task here"
          />
          <input
            type="number"
            id="time"
            placeholder="Enter the time length"
          />
          <input
            type="number"
            id="stdate"
            placeholder="Enter the start date"
          />
          <button type="submit">Add Todo</button>
        </form>
        <table>
        {loading ? (
          <div>Loading...</div>
        ) : (

          tasklist.map((task) => (
            <tr key={task.id}>
              <td className="todo-text">{task.text}</td>
              <td className="time-num">{task.time}</td>
              <td className="stdate">{task.stdate}</td>

              {/* insert delete button below this line */}
            </tr>
          ))

        )}
        </table>
      </div>
    </div>
  );
}

export default Taskgen;
