import React, { useState, useContext } from "react";
import { AppContext } from './AppContext';
import './style.css';
function Pert(){

const { tasklist, dispatch, loading } = useContext(AppContext);
function generatePert(){
    const divs = [];
    for (let i = 0; i < 7; i++) {
      divs.push(<div class="circulo" key={i}>1 {i}</div>);
    }
    return divs;
}
return (

    <div class="contenedor" >{loading ? (
      <div>Loading...</div>
    ) : (generatePert())}</div>
);
}

export default Pert;
